using namespace std;
using ll = long long;
static const auto fastio = []() {
    std::ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
    return 0;
}();

int main() {
    freopen("/Users/genius/Downloads/sample-51.in", "r", stdin);
    int n, k;
    cin >> n >> k;
    vector<int> nums(n);
    for (int i = 0; i < n; i++) {
        float f;
        cin >> f;
        nums[i] = f * 100;
    }

    // find highest avaiable = find lowest unavaliable
    int lo = 0, hi = 10000000;
    while (lo < hi) {
        int mid = (lo + hi) / 2, count = 0;
        for (int v: nums) {
            count += v / mid;
        }
        // cout << lo << " " << hi << " " << n << endl;
        if (count >= k) {
            lo = mid + 1;
        } else {
            hi = mid;
        }
    }
    // cout << lo - 0.01 << endl;
    printf("%.2f\n", (lo - 1) / 100.0);
//    printf("%.2f\n", ans);
//    printf("%.2f\n", (int)ans*100/100.0);
    return 0;
}

